#https://stackoverflow.com/questions/35366623/python-merge-two-csv-files-on-multiple-columns-and-nearest-datetime#https://pandas.pydata.org/pandas-docs/stable/reference/api/pandas.merge_asof.html#pandas.merge_asofimport pandas as pdright = pd.read_csv('capacitance-capture.csv', sep=';')left = pd.read_csv('optitrack-capture.csv', sep=';')left['mergeTime'] = pd.to_numeric(left['roundedTime'])right['mergeTime'] = pd.to_numeric(right['time'])merged = pd.merge_asof(left, right, right_on='mergeTime', left_on='mergeTime', direction='nearest', tolerance=1000)merged.to_csv('output.csv', sep=';')#merged.rename(columns={"x": "index"})print(merged)